void notas_atualizaPosicao(s16 *posicao, u8 velocidade, u8 tamanho)
{
    u16 i;
    for(i = 0; i < tamanho; i++)
    {
        posicao[i] = posicao[i] + velocidade;
    }
}

Nota notas_atualizar(Musica m, u16 tempoAtual)
{
    Nota n = 0;
    u16 i;
    for(i = 0; i < m.tamanho; i++) 
    {
        if(m.tempos[i] == tempoAtual)
        {
            n |=  m.notas[i];
        }
    }
    return n;
}


